contracts/SwapContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title SwapContract
 * @dev Immediate spot asset swaps for HyperEVM users via HyperCore integration
 * Allows users without HyperCore accounts to buy spot assets directly
 * 
 * SECURITY FEATURES:
 * - Refund mechanism for failed/expired swaps
 * - Swap expiry timeout (24 hours default)
 * - ReentrancyGuard on all state-changing functions
 */
contract SwapContract is AccessControl, Pausable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    bytes32 public constant EXECUTOR_ROLE = keccak256("EXECUTOR_ROLE");

    // HyperCore integration addresses from official docs
    address constant ORACLE_PRECOMPILE = 0x0000000000000000000000000000000000000807;
    address constant SPOT_BALANCE_PRECOMPILE = 0x0000000000000000000000000000000000000801;
    address constant CORE_WRITER = 0x3333333333333333333333333333333333333333;

    // HYPE system address (special case)
    address constant HYPE_SYSTEM_ADDRESS = 0x2222222222222222222222222222222222222222;

    // Fee configuration
    uint256 public swapFee = 0.001 ether; // 0.001 HYPE
    uint256 public constant MAX_SLIPPAGE = 5000; // 50% in basis points

    // Swap timeout configuration
    uint256 public swapTimeout = 24 hours; // Swaps expire after 24 hours

    // Swap status enum for clarity
    enum SwapStatus { Pending, Completed, Refunded, Failed }

    // Swap tracking
    struct SwapRequest {
        address user;
        uint64 fromToken;
        uint64 toToken;
        uint256 fromAmount;
        uint256 minOutputAmount;
        uint256 slippageBps;  // User-provided slippage in basis points
        uint256 timestamp;
        bool completed;
        SwapStatus status;
        address fromTokenAddress; // Store for refunds
    }

    mapping(uint256 => SwapRequest) public swapRequests;
    mapping(address => uint256[]) public userSwaps;
    uint256 public nextSwapId = 1;

    // Events
    event SwapInitiated(
        uint256 indexed swapId,
        address indexed user,
        uint64 fromToken,
        uint64 toToken,
        uint256 fromAmount,
        uint256 minOutputAmount
    );

    event SwapCompleted(
        uint256 indexed swapId,
        address indexed user,
        uint256 outputAmount
    );

    event SwapRefunded(
        uint256 indexed swapId,
        address indexed user,
        uint256 refundAmount
    );

    event SwapFailed(
        uint256 indexed swapId,
        address indexed user,
        string reason
    );

    event FeeUpdated(uint256 newFee);
    event SwapTimeoutUpdated(uint256 newTimeout);

    event HypeSystemTransfer(address indexed user, uint256 amount);

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(EXECUTOR_ROLE, msg.sender);
    }

    /**
     * @dev Execute immediate swap between any two spot assets
     * @param fromToken Source token spot index
     * @param toToken Destination token spot index  
     * @param fromAmount Amount to swap (in token's native decimals)
     * @param minOutputAmount Minimum amount to receive (slippage protection)
     * @param slippageBps Slippage tolerance in basis points (e.g., 100 = 1%)
     * @param fromTokenAddress ERC20 contract address for fromToken (0x0 for HYPE)
     */
    function executeSwap(
        uint64 fromToken,
        uint64 toToken,
        uint256 fromAmount,
        uint256 minOutputAmount,
        uint256 slippageBps,
        address fromTokenAddress
    ) external payable whenNotPaused nonReentrant returns (uint256 swapId) {
        require(fromAmount > 0, "Amount must be greater than 0");
        require(fromToken != toToken, "Cannot swap same token");
        require(msg.value >= swapFee, "Insufficient fee payment");
        require(slippageBps <= MAX_SLIPPAGE, "Slippage too high");

        // Create swap request
        swapId = nextSwapId++;
        swapRequests[swapId] = SwapRequest({
            user: msg.sender,
            fromToken: fromToken,
            toToken: toToken,
            fromAmount: fromAmount,
            minOutputAmount: minOutputAmount,
            slippageBps: slippageBps,
            timestamp: block.timestamp,
            completed: false,
            status: SwapStatus.Pending,
            fromTokenAddress: fromTokenAddress
        });

        userSwaps[msg.sender].push(swapId);

        // Transfer user's tokens to this contract first
        if (fromToken == 0) { // HYPE
            require(msg.value >= fromAmount + swapFee, "Insufficient HYPE sent");
        } else {
            // For ERC20 tokens, user must approve this contract first
            require(fromTokenAddress != address(0), "Invalid token address");
            IERC20(fromTokenAddress).safeTransferFrom(msg.sender, address(this), fromAmount);
        }

        // Execute the cross-layer swap
        _executeCrossLayerSwap(swapId, fromToken, toToken, fromAmount, fromTokenAddress);

        emit SwapInitiated(swapId, msg.sender, fromToken, toToken, fromAmount, minOutputAmount);
    }

    /**
     * @dev Helper function for users to manually send HYPE to system address
     * Use this if the contract-mediated transfer isn't working
     */
    function sendHypeToSystem() external payable {
        require(msg.value > 0, "Must send HYPE");
        address systemAddress = HYPE_SYSTEM_ADDRESS;

        // Try the direct transfer
        (bool success,) = systemAddress.call{value: msg.value}("");
        require(success, "HYPE system transfer failed");

        emit HypeSystemTransfer(msg.sender, msg.value);
    }

    /**
     * @dev Execute cross-layer swap via HyperCore order book
     * Places a limit order to trade fromToken for toToken
     */
    function _executeCrossLayerSwap(
        uint256 swapId,
        uint64 fromToken,
        uint64 toToken,
        uint256 fromAmount,
        address fromTokenAddress
    ) internal {
        // Step 1: Send tokens from HyperEVM to HyperCore via system address
        _sendToHyperCore(fromToken, fromAmount, fromTokenAddress);

        // Step 2: Place limit order on HyperCore spot market to trade tokens
        _placeLimitOrder(swapId, fromToken, toToken, fromAmount);

        // Note: Event emission happens in executeSwap() - removed duplicate here
    }

    /**
     * @dev Place a limit order on HyperCore to trade tokens
     * Uses CoreWriter Action ID 1 (limit order)
     */
    function _placeLimitOrder(uint256 swapId, uint64 fromToken, uint64 toToken, uint256 fromAmount) internal {
        SwapRequest storage swap = swapRequests[swapId];

        // For HYPE â†’ USDC swap, we need to place a sell order for HYPE to get USDC
        // Asset ID for spot trading: 10000 + spot_index
        uint32 spotAsset = uint32(10000 + toToken); // USDC spot asset

        // Get current market price for the target token
        uint256 marketPrice = this.getOraclePrice(uint32(toToken));
        require(marketPrice > 0, "Cannot get market price");

        // Place a market order by setting price with user-provided slippage
        bool isBuy = (fromToken != 0); // If selling HYPE (token 0), we're buying the other token
        uint256 slippageMultiplier = swap.slippageBps; // User's slippage in basis points
        uint256 limitPrice;

        if (isBuy) {
            // Buying: set price above market by user's slippage tolerance
            limitPrice = marketPrice * (10000 + slippageMultiplier) / 10000;
        } else {
            // Selling: set price below market by user's slippage tolerance
            limitPrice = marketPrice * (10000 - slippageMultiplier) / 10000;
        }

        // Ensure limitPrice fits in uint64 (with overflow protection)
        require(limitPrice <= type(uint64).max, "Price overflow");

        // Calculate order size - preserve precision by scaling appropriately
        // fromAmount is in token's native decimals, orderSize needs proper conversion
        // Use uint256 for intermediate calculation to prevent overflow
        uint256 orderSizeCalc = fromAmount;
        require(orderSizeCalc <= type(uint64).max, "Order size overflow");
        uint64 orderSize = uint64(orderSizeCalc);
        require(orderSize > 0, "Order size too small");

        // Encode limit order action
        // Action ID 1: (asset, isBuy, limitPx, sz, reduceOnly, encodedTif, cloid)
        bytes memory actionData = abi.encodePacked(
            uint8(1), // Version 1
            uint24(1), // Action ID 1 (limit order)
            abi.encode(
                spotAsset, // asset (spot market)
                isBuy, // isBuy
                uint64(limitPrice), // limitPx
                orderSize, // sz (order size)
                false, // reduceOnly
                uint8(3), // encodedTif (3 = IOC - Immediate or Cancel for market-like behavior)
                uint128(swapId) // cloid (use swapId as client order id for tracking)
            )
        );

        // Call CoreWriter to place the order
        (bool success,) = CORE_WRITER.call(actionData);
        require(success, "CoreWriter limit order failed");

        // Note: Order execution is asynchronous
        // We'll need a separate mechanism to detect fills and send tokens back
    }

    /**
     * @dev Send tokens from HyperEVM to HyperCore via system address
     */
    function _sendToHyperCore(uint64 token, uint256 amount, address tokenAddress) internal {
        address systemAddress = getTokenSystemAddress(token);

        if (token == 0) { // HYPE - native transfer to system address
            (bool success,) = systemAddress.call{value: amount}("");
            require(success, "HYPE transfer to HyperCore failed");
        } else {
            // ERC20 transfer to system address (using SafeERC20)
            require(tokenAddress != address(0), "Invalid token address");
            IERC20(tokenAddress).safeTransfer(systemAddress, amount);
        }
    }

    /**
     * @dev Get system address for a token
     */
    function getTokenSystemAddress(uint64 tokenIndex) public pure returns (address) {
        if (tokenIndex == 0) { // HYPE special case
            return HYPE_SYSTEM_ADDRESS;
        }

        // Standard pattern: 0x2000...0000 + token index
        return address(uint160(0x2000000000000000000000000000000000000000) + uint160(tokenIndex));
    }

    /**
     * @dev Get oracle price for a token
     */
    function getOraclePrice(uint32 oracleIndex) external view returns (uint256) {
        (bool success, bytes memory result) = ORACLE_PRECOMPILE.staticcall(
            abi.encode(oracleIndex)
        );

        if (!success || result.length == 0) {
            return 0;
        }

        return abi.decode(result, (uint256));
    }

    /**
     * @dev Get spot balance for an address
     */
    function getSpotBalance(address user, uint64 token) external view returns (uint256) {
        (bool success, bytes memory result) = SPOT_BALANCE_PRECOMPILE.staticcall(
            abi.encode(user, token)
        );

        if (!success || result.length == 0) {
            return 0;
        }

        return abi.decode(result, (uint256));
    }

    /**
     * @dev Mark swap as completed (called by executor bot)
     */
    function completeSwap(uint256 swapId, uint256 outputAmount) external onlyRole(EXECUTOR_ROLE) {
        SwapRequest storage swap = swapRequests[swapId];
        require(swap.status == SwapStatus.Pending, "Swap not pending");
        require(outputAmount >= swap.minOutputAmount, "Output below minimum");

        swap.completed = true;
        swap.status = SwapStatus.Completed;

        emit SwapCompleted(swapId, swap.user, outputAmount);
    }

    /**
     * @dev Mark swap as failed (called by executor bot when order fails)
     * This allows the user to request a refund
     */
    function markSwapFailed(uint256 swapId, string calldata reason) external onlyRole(EXECUTOR_ROLE) {
        SwapRequest storage swap = swapRequests[swapId];
        require(swap.status == SwapStatus.Pending, "Swap not pending");

        swap.status = SwapStatus.Failed;

        emit SwapFailed(swapId, swap.user, reason);
    }

    /**
     * @dev Request refund for a failed or expired swap
     * Users can claim refunds if:
     * - Swap was marked as failed by executor
     * - Swap has expired (past timeout) and not completed
     */
    function requestRefund(uint256 swapId) external nonReentrant {
        SwapRequest storage swap = swapRequests[swapId];
        require(swap.user == msg.sender, "Not swap owner");
        require(swap.status != SwapStatus.Completed, "Swap already completed");
        require(swap.status != SwapStatus.Refunded, "Already refunded");

        // Check if swap is failed or expired
        bool isExpired = block.timestamp > swap.timestamp + swapTimeout;
        bool isFailed = swap.status == SwapStatus.Failed;
        require(isExpired || isFailed, "Swap not eligible for refund");

        // Mark as refunded before transfer (CEI pattern)
        swap.status = SwapStatus.Refunded;

        // Refund the tokens
        if (swap.fromToken == 0) { // HYPE
            (bool success,) = msg.sender.call{value: swap.fromAmount}("");
            require(success, "HYPE refund failed");
        } else {
            // ERC20 refund
            require(swap.fromTokenAddress != address(0), "Invalid token address");
            IERC20(swap.fromTokenAddress).safeTransfer(msg.sender, swap.fromAmount);
        }

        emit SwapRefunded(swapId, msg.sender, swap.fromAmount);
    }

    /**
     * @dev Check if a swap is eligible for refund
     */
    function isRefundable(uint256 swapId) external view returns (bool eligible, string memory reason) {
        SwapRequest storage swap = swapRequests[swapId];

        if (swap.status == SwapStatus.Completed) {
            return (false, "Swap completed");
        }
        if (swap.status == SwapStatus.Refunded) {
            return (false, "Already refunded");
        }
        if (swap.status == SwapStatus.Failed) {
            return (true, "Swap failed");
        }
        if (block.timestamp > swap.timestamp + swapTimeout) {
            return (true, "Swap expired");
        }
        return (false, "Swap still pending");
    }

    /**
     * @dev Get user's swap history
     */
    function getUserSwaps(address user) external view returns (uint256[] memory) {
        return userSwaps[user];
    }

    /**
     * @dev Get swap details
     */
    function getSwap(uint256 swapId) external view returns (SwapRequest memory) {
        return swapRequests[swapId];
    }

    /**
     * @dev Update swap fee (admin only)
     */
    function updateSwapFee(uint256 newFee) external onlyRole(DEFAULT_ADMIN_ROLE) {
        swapFee = newFee;
        emit FeeUpdated(newFee);
    }

    /**
     * @dev Update swap timeout (admin only)
     * @param newTimeout New timeout in seconds (minimum 1 hour, maximum 7 days)
     */
    function updateSwapTimeout(uint256 newTimeout) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newTimeout >= 1 hours, "Timeout too short");
        require(newTimeout <= 7 days, "Timeout too long");
        swapTimeout = newTimeout;
        emit SwapTimeoutUpdated(newTimeout);
    }

    /**
     * @dev Withdraw collected fees (admin only)
     */
    function withdrawFees() external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 balance = address(this).balance;
        require(balance > 0, "No fees to withdraw");

        (bool success,) = msg.sender.call{value: balance}("");
        require(success, "Fee withdrawal failed");
    }

    /**
     * @dev Pause contract (admin only)
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause contract (admin only)
     */
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Emergency token recovery (admin only)
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (token == address(0)) {
            (bool success,) = msg.sender.call{value: amount}("");
            require(success, "ETH withdrawal failed");
        } else {
            IERC20(token).safeTransfer(msg.sender, amount);
        }
    }

    // Receive function to accept HYPE payments
    receive() external payable {}
}

contracts/TriggerContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title L1Read Interface
 * @dev Interface for HyperCore oracle precompile - provides real-time price data
 */
interface IL1Read {
    // Get oracle price for perp asset (returns price * 10^6 / 10^szDecimals)
    function oraclePx(uint32 index) external view returns (uint64);

    // Get oracle price for spot asset (returns price * 10^8 / 10^baseAssetSzDecimals)
    function spotOraclePx(uint32 index) external view returns (uint64);
}

/**
 * @title TriggerContract
 * @dev Stores price-based trigger orders for HyperCore spot trading
 * 
 * ARCHITECTURE:
 * - This contract ONLY stores trigger parameters (no funds held)
 * - User's actual funds remain in their HyperCore spot balance
 * - User must authorize the worker as an "agent" on Hyperliquid
 * - When trigger conditions are met, worker executes trade via Hyperliquid API
 * - Oracle prices are verified on-chain via HyperCore precompile
 * 
 * FLOW:
 * 1. User authorizes worker as agent on Hyperliquid (one-time, off-chain)
 * 2. User creates trigger here (stores intent on-chain)
 * 3. Worker monitors triggers + prices
 * 4. Condition met â†’ Worker executes trade via HL API on user's behalf
 * 5. Worker marks trigger as executed here, price verified via oracle
 */
contract TriggerContract is AccessControl, Pausable, ReentrancyGuard {
    bytes32 public constant EXECUTOR_ROLE = keccak256("EXECUTOR_ROLE");

    // HyperCore oracle precompile for price verification (0x807)
    IL1Read constant ORACLE = IL1Read(0x0000000000000000000000000000000000000807);

    // Configuration
    uint256 public triggerFee = 0.001 ether; // Small fee to prevent spam
    uint256 public constant MAX_TRIGGER_DURATION = 30 days;
    uint256 public constant MIN_TRIGGER_DURATION = 1 hours;

    // Maximum price deviation allowed between worker price and oracle price (in basis points)
    // 200 = 2% max deviation
    uint256 public maxPriceDeviation = 200;

    // Whether to require oracle verification for execution (default: false for compatibility)
    // When enabled, markExecuted will revert if oracle price deviates too much
    bool public requireOracleVerification = false;

    enum TriggerStatus { Active, Executed, Cancelled, Expired, Failed }

    /**
     * @dev Trigger order parameters
     * NOTE: No funds are held by contract - this is just the order intent
     */
    struct Trigger {
        uint256 id;
        address user;              // User who created the trigger

        // Price monitoring
        string watchAsset;         // Asset to watch for price (e.g., "BTC")
        uint256 targetPrice;       // Price level to trigger at (6 decimals, like Hyperliquid)
        bool isAbove;              // true = trigger when >= price, false = when <= price

        // Trade to execute when triggered
        string tradeAsset;         // Asset to buy/sell (e.g., "FARTCOIN")
        bool isBuy;                // true = buy with USDC, false = sell for USDC
        uint256 amount;            // Amount in asset's native decimals (for buy: USDC amount, for sell: token amount)
        uint256 maxSlippage;       // Max slippage in basis points (e.g., 100 = 1%)

        // Metadata
        uint256 createdAt;
        uint256 expiresAt;
        TriggerStatus status;
        uint256 feePaid;           // Fee paid at creation (for accurate refunds)

        // Execution details (filled when executed)
        uint256 executedAt;
        uint256 executionPrice;
        bytes32 executionTxHash;   // Hyperliquid order ID or tx hash
    }

    // Storage
    mapping(uint256 => Trigger) public triggers;
    mapping(address => uint256[]) public userTriggers;
    uint256 public nextTriggerId = 1;
    uint256 public activeTriggerCount;  // Counter for active triggers (O(1) lookup)

    // Asset symbol to oracle index mapping (e.g., "HYPE" => 1035 for testnet spot)
    // Use spotOraclePx for spot assets
    mapping(string => uint32) public assetToSpotIndex;

    // Events
    event TriggerCreated(
        uint256 indexed triggerId,
        address indexed user,
        string watchAsset,
        uint256 targetPrice,
        bool isAbove,
        string tradeAsset,
        bool isBuy,
        uint256 amount
    );

    event TriggerExecuted(
        uint256 indexed triggerId,
        address indexed executor,
        uint256 executionPrice,
        bytes32 executionTxHash
    );

    event TriggerCancelled(uint256 indexed triggerId, address indexed user);
    event TriggerExpired(uint256 indexed triggerId);
    event TriggerFailed(uint256 indexed triggerId, string reason);
    event FeeUpdated(uint256 newFee);
    event AssetIndexSet(string asset, uint32 spotIndex);
    event PriceDeviationUpdated(uint256 newMaxDeviation);
    event OracleVerificationUpdated(bool required);

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(EXECUTOR_ROLE, msg.sender);
    }

    /**
     * @dev Create a new price-based trigger order
     * @param watchAsset Asset to monitor for price (e.g., "BTC", "ETH")
     * @param targetPrice Target price in 6 decimals (Hyperliquid format)
     * @param isAbove true = trigger when price >= target, false = when <= target
     * @param tradeAsset Asset to trade when triggered (e.g., "FARTCOIN", "HYPE")
     * @param isBuy true = buy tradeAsset with USDC, false = sell tradeAsset for USDC
     * @param amount Amount to trade (USDC amount for buy, token amount for sell)
     * @param maxSlippage Maximum slippage in basis points (100 = 1%)
     * @param durationHours How long the trigger is valid
     */
    function createTrigger(
        string calldata watchAsset,
        uint256 targetPrice,
        bool isAbove,
        string calldata tradeAsset,
        bool isBuy,
        uint256 amount,
        uint256 maxSlippage,
        uint256 durationHours
    ) external payable whenNotPaused nonReentrant returns (uint256 triggerId) {
        require(bytes(watchAsset).length > 0, "Watch asset required");
        require(bytes(tradeAsset).length > 0, "Trade asset required");
        require(targetPrice > 0, "Target price must be > 0");
        require(amount > 0, "Amount must be > 0");
        require(maxSlippage <= 5000, "Max slippage too high"); // Max 50%
        require(durationHours >= MIN_TRIGGER_DURATION / 1 hours, "Duration too short");
        require(durationHours <= MAX_TRIGGER_DURATION / 1 hours, "Duration too long");
        require(msg.value >= triggerFee, "Insufficient trigger fee");

        triggerId = nextTriggerId++;
        uint256 expiresAt = block.timestamp + (durationHours * 1 hours);

        triggers[triggerId] = Trigger({
            id: triggerId,
            user: msg.sender,
            watchAsset: watchAsset,
            targetPrice: targetPrice,
            isAbove: isAbove,
            tradeAsset: tradeAsset,
            isBuy: isBuy,
            amount: amount,
            maxSlippage: maxSlippage,
            createdAt: block.timestamp,
            expiresAt: expiresAt,
            status: TriggerStatus.Active,
            feePaid: msg.value,  // Store actual fee paid for accurate refunds
            executedAt: 0,
            executionPrice: 0,
            executionTxHash: bytes32(0)
        });

        userTriggers[msg.sender].push(triggerId);
        activeTriggerCount++;  // Increment counter

        emit TriggerCreated(
            triggerId,
            msg.sender,
            watchAsset,
            targetPrice,
            isAbove,
            tradeAsset,
            isBuy,
            amount
        );
    }

    /**
     * @dev Get oracle price for an asset
     * @param asset Asset symbol (e.g., "HYPE")
     * @return price Oracle price (8 decimals for spot)
     * @return valid Whether the oracle returned a valid price
     */
    function getOraclePrice(string memory asset) public view returns (uint64 price, bool valid) {
        uint32 spotIndex = assetToSpotIndex[asset];
        if (spotIndex == 0) {
            return (0, false);
        }

        try ORACLE.spotOraclePx(spotIndex) returns (uint64 oraclePrice) {
            return (oraclePrice, oraclePrice > 0);
        } catch {
            return (0, false);
        }
    }

    /**
     * @dev Verify execution price against oracle within acceptable deviation
     * @param asset Asset to verify
     * @param executionPrice Price reported by worker (6 decimals)
     * @return valid Whether the price is within acceptable bounds
     */
    function verifyPriceWithOracle(string memory asset, uint256 executionPrice) public view returns (bool valid) {
        (uint64 oraclePrice, bool oracleValid) = getOraclePrice(asset);

        // If no oracle mapping, skip verification (allow execution)
        if (!oracleValid) {
            return true;
        }

        // Convert oracle price (8 decimals) to 6 decimals for comparison
        uint256 normalizedOraclePrice = uint256(oraclePrice) / 100;

        // Calculate deviation: |executionPrice - oraclePrice| / oraclePrice * 10000
        uint256 deviation;
        if (executionPrice > normalizedOraclePrice) {
            deviation = ((executionPrice - normalizedOraclePrice) * 10000) / normalizedOraclePrice;
        } else {
            deviation = ((normalizedOraclePrice - executionPrice) * 10000) / normalizedOraclePrice;
        }

        return deviation <= maxPriceDeviation;
    }

    /**
     * @dev Mark trigger as executed (called by worker after executing trade on Hyperliquid)
     * NOTE: Price verification happens BEFORE trading in the worker, not here.
     * This function just records what happened - the trade already executed.
     * If requireOracleVerification is enabled, will verify execution price against oracle.
     * @param triggerId The trigger to mark executed
     * @param executionPrice The price at which the trade was executed
     * @param executionTxHash The Hyperliquid order ID or transaction reference
     */
    function markExecuted(
        uint256 triggerId,
        uint256 executionPrice,
        bytes32 executionTxHash
    ) external onlyRole(EXECUTOR_ROLE) {
        Trigger storage trigger = triggers[triggerId];
        require(trigger.status == TriggerStatus.Active, "Trigger not active");

        // Optional oracle verification (when enabled)
        if (requireOracleVerification) {
            bool priceValid = verifyPriceWithOracle(trigger.tradeAsset, executionPrice);
            require(priceValid, "Execution price deviates from oracle");
        }

        trigger.status = TriggerStatus.Executed;
        trigger.executedAt = block.timestamp;
        trigger.executionPrice = executionPrice;
        trigger.executionTxHash = executionTxHash;
        if (activeTriggerCount > 0) activeTriggerCount--;  // Safe decrement

        emit TriggerExecuted(triggerId, msg.sender, executionPrice, executionTxHash);
    }

    /**
     * @dev Mark trigger as failed (called by worker if trade fails)
     * @param triggerId The trigger that failed
     * @param reason Why it failed
     */
    function markFailed(uint256 triggerId, string calldata reason) external onlyRole(EXECUTOR_ROLE) {
        Trigger storage trigger = triggers[triggerId];
        require(trigger.status == TriggerStatus.Active, "Trigger not active");

        trigger.status = TriggerStatus.Failed;
        if (activeTriggerCount > 0) activeTriggerCount--;  // Safe decrement

        emit TriggerFailed(triggerId, reason);
    }

    /**
     * @dev Cancel an active trigger (user only)
     */
    function cancelTrigger(uint256 triggerId) external nonReentrant {
        Trigger storage trigger = triggers[triggerId];
        require(trigger.user == msg.sender, "Not trigger owner");
        require(trigger.status == TriggerStatus.Active, "Trigger not active");

        // Store fee before state changes (CEI pattern)
        uint256 refundAmount = trigger.feePaid;

        // Update state first
        trigger.status = TriggerStatus.Cancelled;
        if (activeTriggerCount > 0) activeTriggerCount--;  // Safe decrement

        // Refund the actual fee paid (not current triggerFee)
        if (refundAmount > 0) {
            (bool success,) = msg.sender.call{value: refundAmount}("");
            require(success, "Fee refund failed");
        }

        emit TriggerCancelled(triggerId, msg.sender);
    }

    /**
     * @dev Update a trigger's price in a single atomic transaction
     * Cancels the old trigger and creates a new one with updated price
     * @param oldTriggerId The trigger to update
     * @param newTargetPrice New target price (6 decimals)
     * @param newIsAbove New direction (true = above, false = below)
     */
    function updateTriggerPrice(
        uint256 oldTriggerId,
        uint256 newTargetPrice,
        bool newIsAbove
    ) external payable whenNotPaused nonReentrant returns (uint256 newTriggerId) {
        Trigger storage oldTrigger = triggers[oldTriggerId];
        require(oldTrigger.user == msg.sender, "Not trigger owner");
        require(oldTrigger.status == TriggerStatus.Active, "Trigger not active");
        require(newTargetPrice > 0, "Target price must be > 0");

        // Store old trigger data before cancellation
        string memory watchAsset = oldTrigger.watchAsset;
        string memory tradeAsset = oldTrigger.tradeAsset;
        bool isBuy = oldTrigger.isBuy;
        uint256 amount = oldTrigger.amount;
        uint256 maxSlippage = oldTrigger.maxSlippage;
        uint256 oldFeePaid = oldTrigger.feePaid;

        // Cancel old trigger (update state)
        oldTrigger.status = TriggerStatus.Cancelled;
        if (activeTriggerCount > 0) activeTriggerCount--;  // Safe decrement

        emit TriggerCancelled(oldTriggerId, msg.sender);

        // Calculate total fee available (refund + any additional payment)
        uint256 totalFee = oldFeePaid + msg.value;
        require(totalFee >= triggerFee, "Insufficient trigger fee");

        // Create new trigger with same duration (24 hours from now)
        newTriggerId = nextTriggerId++;
        uint256 expiresAt = block.timestamp + (24 hours);

        triggers[newTriggerId] = Trigger({
            id: newTriggerId,
            user: msg.sender,
            watchAsset: watchAsset,
            targetPrice: newTargetPrice,
            isAbove: newIsAbove,
            tradeAsset: tradeAsset,
            isBuy: isBuy,
            amount: amount,
            maxSlippage: maxSlippage,
            createdAt: block.timestamp,
            expiresAt: expiresAt,
            status: TriggerStatus.Active,
            feePaid: totalFee,
            executedAt: 0,
            executionPrice: 0,
            executionTxHash: bytes32(0)
        });

        userTriggers[msg.sender].push(newTriggerId);
        activeTriggerCount++;

        emit TriggerCreated(
            newTriggerId,
            msg.sender,
            watchAsset,
            newTargetPrice,
            newIsAbove,
            tradeAsset,
            isBuy,
            amount
        );
    }

    /**
     * @dev Check if trigger conditions are met
     * @param triggerId The trigger to check
     * @param currentPrice Current price of the watch asset (6 decimals)
     */
    function checkTrigger(uint256 triggerId, uint256 currentPrice) 
        external 
        view 
        returns (bool shouldExecute, string memory reason) 
    {
        Trigger storage trigger = triggers[triggerId];

        if (trigger.status != TriggerStatus.Active) {
            return (false, "Trigger not active");
        }

        if (block.timestamp > trigger.expiresAt) {
            return (false, "Trigger expired");
        }

        if (currentPrice == 0) {
            return (false, "Invalid price");
        }

        bool conditionMet = trigger.isAbove ? 
            currentPrice >= trigger.targetPrice : 
            currentPrice <= trigger.targetPrice;

        if (!conditionMet) {
            return (false, "Price condition not met");
        }

        return (true, "Ready to execute");
    }

    /**
     * @dev Clean up expired triggers
     */
    function markExpired(uint256[] calldata triggerIds) external {
        for (uint256 i = 0; i < triggerIds.length; i++) {
            Trigger storage trigger = triggers[triggerIds[i]];

            if (trigger.status == TriggerStatus.Active && block.timestamp > trigger.expiresAt) {
                trigger.status = TriggerStatus.Expired;
                if (activeTriggerCount > 0) activeTriggerCount--;  // Safe decrement
                emit TriggerExpired(triggerIds[i]);
            }
        }
    }

    // ============================================
    // View Functions
    // ============================================

    function getTrigger(uint256 triggerId) external view returns (Trigger memory) {
        return triggers[triggerId];
    }

    function getUserTriggers(address user) external view returns (uint256[] memory) {
        return userTriggers[user];
    }

    function getUserActiveTriggers(address user) external view returns (Trigger[] memory) {
        uint256[] memory ids = userTriggers[user];
        uint256 activeCount = 0;

        // Count active triggers
        for (uint256 i = 0; i < ids.length; i++) {
            if (triggers[ids[i]].status == TriggerStatus.Active) {
                activeCount++;
            }
        }

        // Build result array
        Trigger[] memory result = new Trigger[](activeCount);
        uint256 j = 0;
        for (uint256 i = 0; i < ids.length; i++) {
            if (triggers[ids[i]].status == TriggerStatus.Active) {
                result[j++] = triggers[ids[i]];
            }
        }

        return result;
    }

    function getActiveTriggerCount() external view returns (uint256) {
        return activeTriggerCount;  // O(1) lookup using counter
    }

    // ============================================
    // Admin Functions
    // ============================================

    /**
     * @dev Recalculate activeTriggerCount by scanning triggers
     * Use this if the counter gets out of sync
     * @param startId Starting trigger ID to scan from
     * @param endId Ending trigger ID to scan to (exclusive)
     */
    function recalculateActiveTriggerCount(uint256 startId, uint256 endId) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(endId <= nextTriggerId, "End ID too high");
        require(startId < endId, "Invalid range");

        uint256 count = 0;
        for (uint256 i = startId; i < endId; i++) {
            if (triggers[i].status == TriggerStatus.Active) {
                count++;
            }
        }
        activeTriggerCount = count;
    }

    /**
     * @dev Enable or disable oracle verification for execution
     * When enabled, markExecuted will verify the execution price against the oracle
     */
    function setRequireOracleVerification(bool required) external onlyRole(DEFAULT_ADMIN_ROLE) {
        requireOracleVerification = required;
        emit OracleVerificationUpdated(required);
    }

    function updateTriggerFee(uint256 newFee) external onlyRole(DEFAULT_ADMIN_ROLE) {
        triggerFee = newFee;
        emit FeeUpdated(newFee);
    }

    /**
     * @dev Set oracle index for an asset (for price verification)
     * @param asset Asset symbol (e.g., "HYPE")
     * @param spotIndex Spot oracle index from HyperCore
     */
    function setAssetIndex(string calldata asset, uint32 spotIndex) external onlyRole(DEFAULT_ADMIN_ROLE) {
        assetToSpotIndex[asset] = spotIndex;
        emit AssetIndexSet(asset, spotIndex);
    }

    /**
     * @dev Batch set oracle indexes for multiple assets
     */
    function setAssetIndexes(string[] calldata assets, uint32[] calldata spotIndexes) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(assets.length == spotIndexes.length, "Array length mismatch");
        for (uint256 i = 0; i < assets.length; i++) {
            assetToSpotIndex[assets[i]] = spotIndexes[i];
            emit AssetIndexSet(assets[i], spotIndexes[i]);
        }
    }

    /**
     * @dev Update max price deviation allowed (in basis points)
     */
    function updateMaxPriceDeviation(uint256 newMaxDeviation) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newMaxDeviation <= 1000, "Deviation too high"); // Max 10%
        maxPriceDeviation = newMaxDeviation;
        emit PriceDeviationUpdated(newMaxDeviation);
    }

    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    function withdrawFees() external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 balance = address(this).balance;
        require(balance > 0, "No fees to withdraw");

        (bool success,) = msg.sender.call{value: balance}("");
        require(success, "Withdrawal failed");
    }

    receive() external payable {}
}

