// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title TriggerTypes - HyperEVM Trigger Data Structures
 * @dev Optimized data structures for HyperCore integration
 * @notice Designed for efficient storage and HyperCore asset handling
 */
library TriggerTypes {

    /**
     * @dev Comparison operators for trigger conditions
     */
    enum ComparisonOperator {
        GreaterThan,         // >
        LessThan,            // <
        GreaterThanOrEqual,  // >=
        LessThanOrEqual      // <=
    }

    /**
     * @dev Action types for trigger execution
     */
    enum ActionType {
        BUY,    // Buy target asset when condition is met
        SELL    // Sell target asset when condition is met
    }

    /**
     * @dev Trigger status for tracking execution state
     */
    enum TriggerStatus {
        ACTIVE,     // Trigger is active and monitoring
        EXECUTED,   // Trigger has been executed successfully
        CANCELLED,  // Trigger was cancelled by user
        EXPIRED,    // Trigger expired without execution
        FAILED      // Trigger execution failed
    }

    /**
     * @dev Main trigger structure optimized for HyperEVM
     */
    struct Trigger {
        uint256 id;                          // Unique trigger identifier
        address user;                        // Trigger creator
        uint32 baseAssetIndex;               // HyperCore asset index to monitor (e.g., BTC = 3)
        uint32 targetFromAssetIndex;         // HyperCore asset index to sell (e.g., USDC = 0)
        uint32 targetToAssetIndex;           // HyperCore asset index to buy (e.g., FARTCOIN = 170)
        ComparisonOperator operator;         // Trigger condition operator
        uint256 triggerPrice;               // Price threshold (18 decimals)
        ActionType action;                   // Buy or sell action
        uint256 amount;                     // Amount to trade (18 decimals)
        uint256 slippageTolerance;          // Max slippage in basis points (e.g., 100 = 1%)
        uint256 expiration;                 // Expiration timestamp (0 = no expiration)
        uint256 executionReward;            // Reward for executor (in HYPE)
        TriggerStatus status;               // Current trigger status
        uint256 createdAt;                  // Creation timestamp
        uint256 executedAt;                 // Execution timestamp (0 if not executed)
        address executor;                   // Address that executed the trigger (0 if not executed)
    }

    /**
     * @dev Execution result data
     */
    struct ExecutionResult {
        uint256 triggerId;                  // Trigger ID that was executed
        uint256 executionPrice;             // Price at execution (18 decimals)
        uint256 amountIn;                   // Actual amount traded in
        uint256 amountOut;                  // Actual amount received
        uint256 slippageUsed;               // Actual slippage experienced (basis points)
        uint256 executorReward;             // Reward paid to executor
        address executor;                   // Address that executed
        uint256 executedAt;                 // Execution timestamp
        bool successful;                    // Whether execution was successful
        string errorMessage;                // Error message if failed
    }

    /**
     * @dev Asset mapping for HyperCore integration
     */
    struct AssetInfo {
        uint32 index;                       // HyperCore asset index
        string symbol;                      // Asset symbol (e.g., "BTC", "ETH")
        address tokenContract;              // ERC20 contract address on HyperEVM (if applicable)
        address systemAddress;              // HyperCore system address for transfers
        uint8 decimals;                     // Token decimals
        bool isActive;                      // Whether asset is active for trading
        uint256 minTradeAmount;             // Minimum trade amount
        uint256 maxTradeAmount;             // Maximum trade amount
    }

    /**
     * @dev Price data from HyperCore
     */
    struct PriceData {
        uint256 price;                      // Price in 18 decimals
        uint256 timestamp;                  // Last update timestamp
        uint32 assetIndex;                  // HyperCore asset index
        uint8 confidence;                   // Price confidence (0-100)
        string source;                      // Price source ("oracle", "mark", "spot")
    }

    /**
     * @dev Configuration parameters
     */
    struct TriggerConfig {
        uint256 minExecutionReward;         // Minimum reward required
        uint256 maxSlippageBps;             // Maximum allowed slippage
        uint256 maxTriggerDuration;         // Maximum trigger duration
        uint256 priceValidityPeriod;        // How long prices are considered valid
        uint256 executionDelay;             // Anti-wick protection delay
        bool paused;                        // Whether trigger creation is paused
    }

    // Events
    event TriggerCreated(
        uint256 indexed triggerId,
        address indexed user,
        uint32 baseAssetIndex,
        uint32 targetFromAssetIndex,
        uint32 targetToAssetIndex,
        ComparisonOperator operator,
        uint256 triggerPrice,
        ActionType action,
        uint256 amount
    );

    event TriggerExecuted(
        uint256 indexed triggerId,
        address indexed executor,
        uint256 executionPrice,
        uint256 amountIn,
        uint256 amountOut,
        uint256 executorReward
    );

    event TriggerCancelled(
        uint256 indexed triggerId,
        address indexed user,
        uint256 refundAmount
    );

    event TriggerExpired(
        uint256 indexed triggerId,
        address indexed user
    );

    event TriggerFailed(
        uint256 indexed triggerId,
        address indexed executor,
        string reason
    );

    event AssetAdded(
        uint32 indexed assetIndex,
        string symbol,
        address tokenContract,
        address systemAddress
    );

    event AssetUpdated(
        uint32 indexed assetIndex,
        bool isActive,
        uint256 minTradeAmount,
        uint256 maxTradeAmount
    );

    event PriceUpdated(
        uint32 indexed assetIndex,
        uint256 price,
        uint256 timestamp,
        string source
    );

    // Custom errors
    error InvalidTriggerParameters();
    error TriggerNotFound();
    error TriggerNotActive();
    error UnauthorizedAccess();
    error TriggerAlreadyExpired();
    error InsufficientFunds();
    error ExecutionFailed(string reason);
    error PriceOracleError();
    error AssetNotSupported(uint32 assetIndex);
    error SlippageTooHigh(uint256 requested, uint256 maximum);
    error AmountTooSmall(uint256 amount, uint256 minimum);
    error AmountTooLarge(uint256 amount, uint256 maximum);
    error ContractPaused();
    error InvalidExecutor();
    error RewardTooSmall(uint256 provided, uint256 required);
    error PriceStale(uint256 lastUpdate, uint256 validityPeriod);
}

