contracts/SimpleTriggerManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title SimpleTriggerManager
 * @dev Simplified trigger management contract for HyperEVM deployment
 */
contract SimpleTriggerManager is AccessControl, ReentrancyGuard, Pausable {
    bytes32 public constant EXECUTOR_ROLE = keccak256("EXECUTOR_ROLE");

    struct Trigger {
        uint256 id;
        address user;
        uint256 baseAssetIndex;
        uint256 targetFromAssetIndex;
        uint256 targetToAssetIndex;
        uint8 operator; // 0=GT, 1=LT, 2=GTE, 3=LTE
        uint256 triggerPrice;
        uint8 action; // 0=BUY, 1=SELL
        uint256 amount;
        uint256 slippageTolerance;
        uint256 executionReward;
        uint8 status; // 0=ACTIVE, 1=EXECUTED, 2=CANCELLED
        uint256 createdAt;
        uint256 executedAt;
        address executor;
    }

    mapping(uint256 => Trigger) public triggers;
    mapping(address => uint256[]) public userTriggers;

    uint256 public nextTriggerId = 1;
    uint256 public constant MIN_EXECUTION_REWARD = 0.01 ether;

    event TriggerCreated(uint256 indexed triggerId, address indexed user);
    event TriggerExecuted(uint256 indexed triggerId, address indexed executor);
    event TriggerCancelled(uint256 indexed triggerId, address indexed user);

    error InsufficientFunds();
    error InvalidTrigger();
    error Unauthorized();
    error TriggerNotActive();

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(EXECUTOR_ROLE, msg.sender);
    }

    function createTrigger(
        uint256 baseAssetIndex,
        uint256 targetFromAssetIndex,
        uint256 targetToAssetIndex,
        uint8 operator,
        uint256 triggerPrice,
        uint8 action,
        uint256 amount,
        uint256 slippageTolerance
    ) external payable nonReentrant whenNotPaused {
        if (msg.value < amount + MIN_EXECUTION_REWARD) revert InsufficientFunds();
        if (triggerPrice == 0 || amount == 0) revert InvalidTrigger();

        uint256 triggerId = nextTriggerId++;

        triggers[triggerId] = Trigger({
            id: triggerId,
            user: msg.sender,
            baseAssetIndex: baseAssetIndex,
            targetFromAssetIndex: targetFromAssetIndex,
            targetToAssetIndex: targetToAssetIndex,
            operator: operator,
            triggerPrice: triggerPrice,
            action: action,
            amount: amount,
            slippageTolerance: slippageTolerance,
            executionReward: MIN_EXECUTION_REWARD,
            status: 0, // ACTIVE
            createdAt: block.timestamp,
            executedAt: 0,
            executor: address(0)
        });

        userTriggers[msg.sender].push(triggerId);

        emit TriggerCreated(triggerId, msg.sender);
    }

    function executeTrigger(uint256 triggerId) external onlyRole(EXECUTOR_ROLE) nonReentrant whenNotPaused {
        Trigger storage trigger = triggers[triggerId];
        if (trigger.status != 0) revert TriggerNotActive();

        trigger.status = 1; // EXECUTED
        trigger.executedAt = block.timestamp;
        trigger.executor = msg.sender;

        // Transfer execution reward to executor
        payable(msg.sender).transfer(trigger.executionReward);

        emit TriggerExecuted(triggerId, msg.sender);
    }

    function cancelTrigger(uint256 triggerId) external nonReentrant {
        Trigger storage trigger = triggers[triggerId];
        if (trigger.user != msg.sender && !hasRole(DEFAULT_ADMIN_ROLE, msg.sender)) revert Unauthorized();
        if (trigger.status != 0) revert TriggerNotActive();

        trigger.status = 2; // CANCELLED

        // Refund user
        uint256 refundAmount = trigger.amount + trigger.executionReward;
        payable(trigger.user).transfer(refundAmount);

        emit TriggerCancelled(triggerId, trigger.user);
    }

    function getTrigger(uint256 triggerId) external view returns (Trigger memory) {
        return triggers[triggerId];
    }

    function getUserTriggers(address user) external view returns (uint256[] memory) {
        return userTriggers[user];
    }

    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    // Emergency withdrawal for admin
    function emergencyWithdraw() external onlyRole(DEFAULT_ADMIN_ROLE) {
        payable(msg.sender).transfer(address(this).balance);
    }
}

contracts/TriggerManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./L1Read.sol";
import "./libraries/TriggerTypes.sol";

/**
 * @title TriggerManager - HyperEVM Cross-Layer Trigger System
 * @dev Leverages HyperCore precompiles for real-time price feeds and hybrid execution
 * @notice First-ever cross-layer DeFi application bridging HyperEVM smart contracts with HyperCore liquidity
 * 
 * Architecture:
 * 1. Smart contracts handle trigger logic, fund escrow, and price monitoring via precompiles
 * 2. Off-chain executor bots execute trades on HyperCore when conditions are met
 * 3. Results are verified and distributed on-chain
 * 
 * Benefits:
 * - Access to $320M+ daily HyperCore liquidity
 * - Real-time order book prices (no external oracles needed)
 * - Lower execution costs (HyperCore vs EVM gas)
 * - MEV protection through API execution
 */
contract TriggerManager is 
    Initializable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    L1Read
{
    using SafeERC20 for IERC20;
    using TriggerTypes for TriggerTypes.Trigger;

    // Roles
    bytes32 public constant EXECUTOR_ROLE = keccak256("EXECUTOR_ROLE");
    bytes32 public constant ASSET_MANAGER_ROLE = keccak256("ASSET_MANAGER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    // State
    mapping(uint256 => TriggerTypes.Trigger) public triggers;
    mapping(address => uint256[]) public userTriggers;
    mapping(uint32 => TriggerTypes.AssetInfo) public supportedAssets;
    mapping(uint256 => TriggerTypes.ExecutionResult) public executionResults;
    mapping(uint32 => TriggerTypes.PriceData) public latestPrices;

    uint256 public nextTriggerId;
    uint256 public totalActiveTriggers;
    TriggerTypes.TriggerConfig public config;

    // Constants
    uint256 private constant PRICE_DECIMALS = 18;
    uint256 private constant BASIS_POINTS = 10000;
    uint256 private constant MAX_SLIPPAGE_BPS = 2000; // 20%
    uint256 private constant MIN_EXECUTION_DELAY = 30; // 30 seconds anti-wick protection

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address admin,
        uint256 _minExecutionReward,
        uint256 _maxSlippageBps,
        uint256 _maxTriggerDuration
    ) public initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();
        __Pausable_init();

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(PAUSER_ROLE, admin);
        _grantRole(ASSET_MANAGER_ROLE, admin);

        config = TriggerTypes.TriggerConfig({
            minExecutionReward: _minExecutionReward,
            maxSlippageBps: _maxSlippageBps > MAX_SLIPPAGE_BPS ? MAX_SLIPPAGE_BPS : _maxSlippageBps,
            maxTriggerDuration: _maxTriggerDuration,
            priceValidityPeriod: 300, // 5 minutes
            executionDelay: MIN_EXECUTION_DELAY,
            paused: false
        });

        nextTriggerId = 1;
    }

    /**
     * @dev Create a new trigger with HyperCore asset indices
     * @param baseAssetIndex HyperCore asset index to monitor (e.g., 3 for BTC)
     * @param targetFromAssetIndex HyperCore asset index to sell (e.g., 0 for USDC)
     * @param targetToAssetIndex HyperCore asset index to buy (e.g., 170 for FARTCOIN)
     * @param operator Comparison operator for trigger condition
     * @param triggerPrice Price threshold in 18 decimals
     * @param action Buy or sell action
     * @param amount Amount to trade in 18 decimals
     * @param slippageTolerance Maximum slippage in basis points
     * @param expiration Expiration timestamp (0 for no expiration)
     */
    function createTrigger(
        uint32 baseAssetIndex,
        uint32 targetFromAssetIndex,
        uint32 targetToAssetIndex,
        TriggerTypes.ComparisonOperator operator,
        uint256 triggerPrice,
        TriggerTypes.ActionType action,
        uint256 amount,
        uint256 slippageTolerance,
        uint256 expiration
    ) external payable nonReentrant whenNotPaused {
        // Validate parameters
        if (triggerPrice == 0 || amount == 0) revert TriggerTypes.InvalidTriggerParameters();
        if (slippageTolerance > config.maxSlippageBps) revert TriggerTypes.SlippageTooHigh(slippageTolerance, config.maxSlippageBps);
        if (msg.value < config.minExecutionReward) revert TriggerTypes.RewardTooSmall(msg.value, config.minExecutionReward);
        if (expiration != 0 && expiration <= block.timestamp) revert TriggerTypes.InvalidTriggerParameters();
        if (expiration != 0 && expiration > block.timestamp + config.maxTriggerDuration) revert TriggerTypes.InvalidTriggerParameters();

        // Validate assets are supported
        if (!supportedAssets[baseAssetIndex].isActive) revert TriggerTypes.AssetNotSupported(baseAssetIndex);
        if (!supportedAssets[targetFromAssetIndex].isActive) revert TriggerTypes.AssetNotSupported(targetFromAssetIndex);
        if (!supportedAssets[targetToAssetIndex].isActive) revert TriggerTypes.AssetNotSupported(targetToAssetIndex);

        // Validate trade amount
        TriggerTypes.AssetInfo memory fromAsset = supportedAssets[targetFromAssetIndex];
        if (amount < fromAsset.minTradeAmount) revert TriggerTypes.AmountTooSmall(amount, fromAsset.minTradeAmount);
        if (amount > fromAsset.maxTradeAmount) revert TriggerTypes.AmountTooLarge(amount, fromAsset.maxTradeAmount);

        // Escrow funds if not native HYPE
        if (targetFromAssetIndex != 150) { // 150 is HYPE index
            IERC20(fromAsset.tokenContract).safeTransferFrom(msg.sender, address(this), amount);
        }

        uint256 triggerId = nextTriggerId++;

        triggers[triggerId] = TriggerTypes.Trigger({
            id: triggerId,
            user: msg.sender,
            baseAssetIndex: baseAssetIndex,
            targetFromAssetIndex: targetFromAssetIndex,
            targetToAssetIndex: targetToAssetIndex,
            operator: operator,
            triggerPrice: triggerPrice,
            action: action,
            amount: amount,
            slippageTolerance: slippageTolerance,
            expiration: expiration,
            executionReward: msg.value,
            status: TriggerTypes.TriggerStatus.ACTIVE,
            createdAt: block.timestamp,
            executedAt: 0,
            executor: address(0)
        });

        userTriggers[msg.sender].push(triggerId);
        totalActiveTriggers++;

        emit TriggerTypes.TriggerCreated(
            triggerId,
            msg.sender,
            baseAssetIndex,
            targetFromAssetIndex,
            targetToAssetIndex,
            operator,
            triggerPrice,
            action,
            amount
        );
    }

    /**
     * @dev Check if trigger condition is met and execute if valid
     * @param triggerId The ID of the trigger to execute
     */
    function executeTrigger(uint256 triggerId) 
        external 
        nonReentrant 
        whenNotPaused 
        onlyRole(EXECUTOR_ROLE)
    {
        TriggerTypes.Trigger storage trigger = triggers[triggerId];

        if (trigger.status != TriggerTypes.TriggerStatus.ACTIVE) revert TriggerTypes.TriggerNotActive();
        if (trigger.expiration != 0 && block.timestamp > trigger.expiration) {
            trigger.status = TriggerTypes.TriggerStatus.EXPIRED;
            totalActiveTriggers--;
            _refundTrigger(triggerId);
            emit TriggerTypes.TriggerExpired(triggerId, trigger.user);
            return;
        }

        // Get current price from HyperCore via precompiles
        uint256 currentPrice = _getHyperCorePrice(trigger.baseAssetIndex);

        // Update price data
        latestPrices[trigger.baseAssetIndex] = TriggerTypes.PriceData({
            price: currentPrice,
            timestamp: block.timestamp,
            assetIndex: trigger.baseAssetIndex,
            confidence: 100, // HyperCore prices are always high confidence
            source: "hypercore"
        });

        // Check if trigger condition is met
        bool conditionMet = _checkTriggerCondition(
            currentPrice,
            trigger.triggerPrice,
            trigger.operator
        );

        if (!conditionMet) revert TriggerTypes.ExecutionFailed("Trigger condition not met");

        // Anti-wick protection: ensure price has been stable
        if (block.timestamp < trigger.createdAt + config.executionDelay) {
            revert TriggerTypes.ExecutionFailed("Execution delay not met");
        }

        // Mark as executed and prepare for off-chain execution
        trigger.status = TriggerTypes.TriggerStatus.EXECUTED;
        trigger.executedAt = block.timestamp;
        trigger.executor = msg.sender;
        totalActiveTriggers--;

        // Transfer funds to HyperCore system address for execution
        TriggerTypes.AssetInfo memory fromAsset = supportedAssets[trigger.targetFromAssetIndex];
        if (trigger.targetFromAssetIndex == 150) { // HYPE
            // Transfer HYPE to system address
            (bool success, ) = fromAsset.systemAddress.call{value: trigger.amount}("");
            require(success, "HYPE transfer failed");
        } else {
            // Transfer ERC20 to system address
            IERC20(fromAsset.tokenContract).safeTransfer(fromAsset.systemAddress, trigger.amount);
        }

        // Pay executor reward
        (bool success, ) = payable(msg.sender).call{value: trigger.executionReward}("");
        require(success, "Executor reward payment failed");

        // Emit event for off-chain executor to complete the trade
        emit TriggerTypes.TriggerExecuted(
            triggerId,
            msg.sender,
            currentPrice,
            trigger.amount,
            0, // amountOut will be updated when trade completes
            trigger.executionReward
        );

        // Store execution result (will be updated by confirmExecution)
        executionResults[triggerId] = TriggerTypes.ExecutionResult({
            triggerId: triggerId,
            executionPrice: currentPrice,
            amountIn: trigger.amount,
            amountOut: 0, // To be updated
            slippageUsed: 0, // To be updated
            executorReward: trigger.executionReward,
            executor: msg.sender,
            executedAt: block.timestamp,
            successful: false, // To be updated
            errorMessage: ""
        });
    }

    /**
     * @dev Confirm execution result from off-chain executor
     * @param triggerId The trigger that was executed
     * @param amountOut The amount received from the trade
     * @param successful Whether the trade was successful
     * @param errorMessage Error message if trade failed
     */
    function confirmExecution(
        uint256 triggerId,
        uint256 amountOut,
        bool successful,
        string calldata errorMessage
    ) external onlyRole(EXECUTOR_ROLE) {
        TriggerTypes.Trigger storage trigger = triggers[triggerId];
        TriggerTypes.ExecutionResult storage result = executionResults[triggerId];

        if (trigger.executor != msg.sender) revert TriggerTypes.InvalidExecutor();
        if (trigger.status != TriggerTypes.TriggerStatus.EXECUTED) revert TriggerTypes.TriggerNotActive();

        result.amountOut = amountOut;
        result.successful = successful;
        result.errorMessage = errorMessage;

        if (successful && amountOut > 0) {
            // Calculate actual slippage
            uint256 expectedOut = trigger.amount; // Simplified - should be calculated based on price
            if (amountOut < expectedOut) {
                result.slippageUsed = ((expectedOut - amountOut) * BASIS_POINTS) / expectedOut;
            }

            // Transfer result tokens to user
            TriggerTypes.AssetInfo memory toAsset = supportedAssets[trigger.targetToAssetIndex];
            if (trigger.targetToAssetIndex == 150) { // HYPE
                (bool success, ) = payable(trigger.user).call{value: amountOut}("");
                require(success, "HYPE transfer to user failed");
            } else {
                IERC20(toAsset.tokenContract).safeTransfer(trigger.user, amountOut);
            }
        } else {
            // Trade failed - refund original funds
            trigger.status = TriggerTypes.TriggerStatus.FAILED;
            _refundTrigger(triggerId);
            emit TriggerTypes.TriggerFailed(triggerId, msg.sender, errorMessage);
        }
    }

    /**
     * @dev Cancel an active trigger and refund funds
     * @param triggerId The trigger to cancel
     */
    function cancelTrigger(uint256 triggerId) external nonReentrant {
        TriggerTypes.Trigger storage trigger = triggers[triggerId];

        if (trigger.user != msg.sender && !hasRole(DEFAULT_ADMIN_ROLE, msg.sender)) {
            revert TriggerTypes.UnauthorizedAccess();
        }
        if (trigger.status != TriggerTypes.TriggerStatus.ACTIVE) revert TriggerTypes.TriggerNotActive();

        trigger.status = TriggerTypes.TriggerStatus.CANCELLED;
        totalActiveTriggers--;

        _refundTrigger(triggerId);

        emit TriggerTypes.TriggerCancelled(triggerId, trigger.user, trigger.amount + trigger.executionReward);
    }

    /**
     * @dev Get current price from HyperCore precompiles with fallback
     * @param assetIndex HyperCore asset index
     * @return price Current price in 18 decimals
     */
    function _getHyperCorePrice(uint32 assetIndex) internal view returns (uint256) {
        try this.oraclePx(assetIndex) returns (uint64 rawPrice) {
            if (rawPrice > 0) {
                return _convertPrice(rawPrice, assetIndex);
            }
        } catch {}

        try this.markPx(assetIndex) returns (uint64 rawPrice) {
            if (rawPrice > 0) {
                return _convertPrice(rawPrice, assetIndex);
            }
        } catch {}

        try this.spotPx(assetIndex) returns (uint64 rawPrice) {
            if (rawPrice > 0) {
                return _convertPrice(rawPrice, assetIndex);
            }
        } catch {}

        revert TriggerTypes.PriceOracleError();
    }

    /**
     * @dev Convert raw HyperCore price to 18 decimals
     * @param rawPrice Raw price from precompile
     * @param assetIndex Asset index to get decimals info
     * @return Converted price in 18 decimals
     */
    function _convertPrice(uint64 rawPrice, uint32 assetIndex) internal view returns (uint256) {
        try this.perpAssetInfo(assetIndex) returns (PerpAssetInfo memory info) {
            uint8 szDecimals = info.szDecimals;
            uint256 divisor = 10 ** (6 - szDecimals);
            return (uint256(rawPrice) * 10**PRICE_DECIMALS) / divisor;
        } catch {
            // Fallback: assume 5 decimals (like BTC)
            return (uint256(rawPrice) * 10**PRICE_DECIMALS) / 10;
        }
    }

    /**
     * @dev Check if trigger condition is satisfied
     */
    function _checkTriggerCondition(
        uint256 currentPrice,
        uint256 triggerPrice,
        TriggerTypes.ComparisonOperator operator
    ) internal pure returns (bool) {
        if (operator == TriggerTypes.ComparisonOperator.GreaterThan) {
            return currentPrice > triggerPrice;
        } else if (operator == TriggerTypes.ComparisonOperator.LessThan) {
            return currentPrice < triggerPrice;
        } else if (operator == TriggerTypes.ComparisonOperator.GreaterThanOrEqual) {
            return currentPrice >= triggerPrice;
        } else if (operator == TriggerTypes.ComparisonOperator.LessThanOrEqual) {
            return currentPrice <= triggerPrice;
        }
        return false;
    }

    /**
     * @dev Refund trigger funds and execution reward to user
     */
    function _refundTrigger(uint256 triggerId) internal {
        TriggerTypes.Trigger storage trigger = triggers[triggerId];

        // Refund escrowed funds
        if (trigger.targetFromAssetIndex == 150) { // HYPE
            (bool success, ) = payable(trigger.user).call{value: trigger.amount}("");
            require(success, "HYPE refund failed");
        } else {
            TriggerTypes.AssetInfo memory fromAsset = supportedAssets[trigger.targetFromAssetIndex];
            IERC20(fromAsset.tokenContract).safeTransfer(trigger.user, trigger.amount);
        }

        // Refund execution reward
        (bool success, ) = payable(trigger.user).call{value: trigger.executionReward}("");
        require(success, "Execution reward refund failed");
    }

    // Asset management functions (ASSET_MANAGER_ROLE)
    function addAsset(
        uint32 assetIndex,
        string calldata symbol,
        address tokenContract,
        address systemAddress,
        uint8 decimals,
        uint256 minTradeAmount,
        uint256 maxTradeAmount
    ) external onlyRole(ASSET_MANAGER_ROLE) {
        supportedAssets[assetIndex] = TriggerTypes.AssetInfo({
            index: assetIndex,
            symbol: symbol,
            tokenContract: tokenContract,
            systemAddress: systemAddress,
            decimals: decimals,
            isActive: true,
            minTradeAmount: minTradeAmount,
            maxTradeAmount: maxTradeAmount
        });

        emit TriggerTypes.AssetAdded(assetIndex, symbol, tokenContract, systemAddress);
    }

    // View functions
    function getTrigger(uint256 triggerId) external view returns (TriggerTypes.Trigger memory) {
        return triggers[triggerId];
    }

    function getUserTriggers(address user) external view returns (uint256[] memory) {
        return userTriggers[user];
    }

    function getExecutionResult(uint256 triggerId) external view returns (TriggerTypes.ExecutionResult memory) {
        return executionResults[triggerId];
    }

    function getCurrentPrice(uint32 assetIndex) external view returns (uint256) {
        return _getHyperCorePrice(assetIndex);
    }

    function checkTriggerCondition(uint256 triggerId) external view returns (bool conditionMet, uint256 currentPrice) {
        TriggerTypes.Trigger memory trigger = triggers[triggerId];
        if (trigger.status != TriggerTypes.TriggerStatus.ACTIVE) return (false, 0);

        try this.getCurrentPrice(trigger.baseAssetIndex) returns (uint256 price) {
            currentPrice = price;
            conditionMet = _checkTriggerCondition(price, trigger.triggerPrice, trigger.operator);
        } catch {
            return (false, 0);
        }
    }

    // Admin functions
    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function updateConfig(TriggerTypes.TriggerConfig calldata newConfig) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (newConfig.maxSlippageBps > MAX_SLIPPAGE_BPS) revert TriggerTypes.SlippageTooHigh(newConfig.maxSlippageBps, MAX_SLIPPAGE_BPS);
        config = newConfig;
    }

    function grantExecutorRole(address executor) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(EXECUTOR_ROLE, executor);
    }

    function revokeExecutorRole(address executor) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _revokeRole(EXECUTOR_ROLE, executor);
    }

    // Emergency functions
    function emergencyWithdraw() external onlyRole(DEFAULT_ADMIN_ROLE) {
        (bool success, ) = payable(msg.sender).call{value: address(this).balance}("");
        require(success, "Emergency withdrawal failed");
    }

    // Receive function for HYPE transfers
    receive() external payable {}
}

